<template>    <div id="category">        <ul>            <li v-for="(item,i) in result" :key="i" >                <span v-for="(index, o) in item" :key="o" @click="getMethod(i, o)">                    {{index}}                </span>            </li>        </ul>    </div></template><script>    export default {        name: "category",        data() {            return {                result: []            }        },        methods: {            getMethod(i, o){                console.log("i",i, "o", o)            }        },        created: function () {            let _this = this;            _this.$nextTick(function () {                this.$axios.post('/menu').then((response) => {                    let data = response.data;                    let length = data.length;                    for (let i = 0; i < length; i += 3) {                        let a = data.slice(i, i+3)                        let b = []                        b.push(a[0])                        b.push(a[1])                        b.push(a[2])                        this.result.push(b)                    }                    console.log(this.result)                })            });        }    }</script><style scoped></style>