package com.duya.shopping.controller;import com.alibaba.fastjson.JSON;import com.duya.shopping.dao.ClassDao;import com.duya.shopping.pojo.ClassEntity;import com.duya.shopping.pojo.UserEntity;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RestController;import java.util.Arrays;import java.util.List;@RestController("/home")public class HomeController {    @Autowired    ClassDao classDao;    @PostMapping("/menu")    public String menu(){        List<ClassEntity> list =  classDao.findAll();        String[] data = new String[list.size()];        for (int i = 0;i < list.size();i++){            data[i] = list.get(i).getName();        }        return JSON.toJSONString(data);    }}